mapper.select:
select goodid as goodId, styleid as styleId, good_name as goodName, good_original_price as goodOriginalPrice, good_pic_path as goodPicPath, good_discounted_price as goodDiscountedPrice, good_desc as goodDesc, specification, free_delivery as freeDelivery, sals_num as salsNum, good_status as goodStatus from good_info
and styleid=#{styleId}
limit #{start},#{ps}

mapper.update:
update good_info
styleid=#{styleId}, good_name=#{goodName}, good_original_price=#{goodOriginalPrice}, good_pic_path=#{goodPicPath}, good_discounted_price=#{goodDiscountedPrice}, good_desc=#{goodDesc}, specification=#{specification}, free_delivery=#{freeDelivery}, good_status=#{goodStatus},
where goodid=#{goodId}
